const likeCounters=document.querySelectorAll(".like-counter_wrapper");likeCounters.forEach((e=>{const t=e.querySelector(".like-counter_like-button"),n=e.querySelector(".like-counter_like-number");let l=!1;t.addEventListener("click",(()=>{if(l){let e=parseInt(n.textContent);e-=1,n.textContent=e,t.classList.remove("liked")}else{let e=parseInt(n.textContent);e+=1,n.textContent=e,t.classList.add("liked")}l=!l}))}));